from   PySiddhi4.sp.SPClient   import   SPClient  spPythonClient   =   SPClient ( http://localhost:9090 )   # host URL of SP  siddhiAppManagementClient   =   spPythonClient . getSiddhiAppManagementClient ()  siddhiApp   =   @App:name( TestSiddhiApp1 )   
                 define stream FooStream(symbol string, price float, volume long);   
                 @source(type= inMemory , topic= symbol , @map(type= passThrough ))   
                 define stream BarStream(symbol string, price float, volume long);  
                 from FooStream select symbol, price, volume insert into BarStream;   if   siddhiAppManagementClient . saveSiddhiApp ( siddhiApp ,   username = admin ,   password = admin ): 
     print ( Successfully saved! )

from   PySiddhi4.sp.SPClient   import   SPClient  from   PySiddhi4.sp.SiddhiAppManagement.SiddhiAppManagementClient   import   UpdateAppStatusResponse  spPythonClient   =   SPClient ( http://localhost:9090 )   # Host URL of SP  siddhiAppManagementClient   =   spPythonClient . getSiddhiAppManagementClient ()  siddhiApp   =   @App:name( TestSiddhiApp1 )   
                 define stream FooStream (symbol string, price float, volume long);   
                 @source(type= inMemory , topic= symbol , @map(type= passThrough ))   
                 define stream BarStream (symbol string, price float, volume long);   
                 from FooStream select symbol, price, volume insert into BarStream;   result   =   siddhiAppManagementClient . updateSiddhiApp ( siddhiApp ,   username = admin ,   password = admin )  if   result . name   ==   UpdateAppStatusResponse . savedNew . name : 
     print ( Saved new Siddhi App )  elif   result . name   ==   UpdateAppStatusResponse . updated . name : 
     print ( Updated saved Siddhi App )
